import React from 'react';
import "./Features.css";
import { BsFillBookmarkStarFill } from 'react-icons/bs';
import phoneFeatures from "../../assets/phone-features.png"
import Feature from './Feature';
import { featureList } from './data.js';


const Features = () => {

  return (
    <section id="features">
      <div className="container features">
        <div className="u-title">
          <BsFillBookmarkStarFill color="orangered" size={30} />
          <h2>Core Features</h2>
          <p className="u-text-small u-text-dark">
            SocialX app has Lorem ipsum dolor sit amet consectetur adipisicing
            elit. Sequi labore, harum officiis error quia cupiditate?
          </p>
        </div>

        <div className="features-content">
          <div className="features-left">
            <img src={phoneFeatures} alt="phone-features" />
          </div>
          <div className="features-right">
            {
              featureList.map((feature)=> {
                <Feature key={feature.id} text={feature.text} icon={feature.icon} heading={feature.heading} />
              })
            }
          </div>
        </div>
      </div>
    </section>
  );
}

export default Features
