import React, {useState} from 'react';
import "./Question.css";
import { AiOutlinePlus, AiOutlineMinus } from "react-icons/ai";

const Question = () => {

    const [showAnswer, setShowAnswer] = useState(false);
    const handleClick = () => {
        setShowAnswer(!showAnswer);
    }
  return (
    <div className="container question">
        <div className="question-title" onClick={handleClick}>
            <h4>Where can I learn ReactJS</h4>
            <button className="question-icon" onClick={handleClick}>
                {
                    showAnswer ? <AiOutlineMinus color="red"/> : <AiOutlinePlus/>
                }
            </button>
        </div>
        <div className="question-answer">
            {showAnswer && <p className="u-text-small">
               Yes, you can learn ReactJS at ZinoTrustAcademy.com 
            </p>}
        </div>
    </div>
  )
}

export default Question
